import { Component } from '@angular/core';
import { AuthService } from '../auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  email: string = '';
  senha: string = '';

  constructor(private authService: AuthService, private router: Router) { }

  onSubmit(): void {
    this.authService.login(this.email, this.senha).subscribe(
      (response : any) => {
        console.log('Autenticação bem-sucedida:', response);

        this.router.navigate(['/cadastro-usuarios']);
      },
      (error : any) => {
        console.error('Erro de autenticação:', error);
      }
    );
  }
}
<div class="container">
  <form (ngSubmit)="onSubmit()">
    <div class="campos">
      <h2>Login de Usuário</h2>

      <label for="email">Email:</label>
      <input type="email" id="email" name="email" class="campo" [(ngModel)]="email" required>

      <label for="senha">Senha:</label>
      <input type="password" id="senha" name="senha" class="campo" [(ngModel)]="senha" required>

      <button type="submit">Login</button>
    </div>
    <div class="logo">
      <img src="assets/logo_resoluti.png" alt="Minha Imagem">
    </div>
  </form>
</div>
