import { Component } from '@angular/core';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  email: string = '';
  senha: string = '';

  onSubmit(): void {
    // Adicione aqui a lógica de autenticação, por exemplo, chamando um serviço
    console.log('Email:', this.email);
    console.log('Senha:', this.senha);
  }
}
<div class="container">
  <form (ngSubmit)="onSubmit()">
    <div class="campos">
      <h2>Login de Usuário</h2>

      <label for="email">Email:</label>
      <input type="email" id="email" name="email" class="campo" [(ngModel)]="email" required>

      <label for="senha">Senha:</label>
      <input type="password" id="senha" name="senha" class="campo" [(ngModel)]="senha" required>

      <button type="submit">Login</button>
    </div>
    <div class="logo">
      <img src="assets/logo_resoluti.png" alt="Minha Imagem">
    </div>
  </form>
</div>
